import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import { useAuth } from '../context/AuthContext';
import './Dashboard.css';

function Dashboard() {
  const { user } = useAuth();
  const [stats, setStats] = useState(null);
  const [recentCases, setRecentCases] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchDashboardData();
  }, []);

  const fetchDashboardData = async () => {
    try {
      const [statsRes, casesRes] = await Promise.all([
        axios.get('/api/cases/stats/overview'),
        axios.get('/api/cases?limit=5')
      ]);
      
      setStats(statsRes.data);
      setRecentCases(casesRes.data.cases.slice(0, 5));
      setLoading(false);
    } catch (error) {
      console.error('Error fetching dashboard data:', error);
      setLoading(false);
    }
  };

  if (loading) {
    return <div className="loading">Loading dashboard...</div>;
  }

  return (
    <div className="container" dir="rtl">
      <div className="page-header">
        <h1>ŸÖÿ±ÿ≠ÿ®ÿßŸã ÿ®ÿπŸàÿØÿ™ŸÉÿå {user?.name}! üëã</h1>
        <p>ŸÜÿ∏ÿ±ÿ© ÿπÿßŸÖÿ© ÿπŸÑŸâ ŸÖŸÉÿ™ÿ® ÿßŸÑŸÖÿ≠ÿßŸÖÿßÿ© ÿßŸÑÿÆÿßÿµ ÿ®ŸÉ</p>
      </div>

      <div className="stats-grid">
        <div className="stat-card">
          <div className="stat-icon">üìÇ</div>
          <div className="stat-content">
            <h3>{stats?.totalCases || 0}</h3>
            <p>ÿ•ÿ¨ŸÖÿßŸÑŸä ÿßŸÑŸÇÿ∂ÿßŸäÿß</p>
          </div>
        </div>
        
        <div className="stat-card">
          <div className="stat-icon">üìã</div>
          <div className="stat-content">
            <h3>{stats?.openCases || 0}</h3>
            <p>ÿßŸÑŸÇÿ∂ÿßŸäÿß ÿßŸÑŸÖŸÅÿ™Ÿàÿ≠ÿ©</p>
          </div>
        </div>
        
        <div className="stat-card">
          <div className="stat-icon">‚ö°</div>
          <div className="stat-content">
            <h3>{stats?.inProgressCases || 0}</h3>
            <p>ŸÇŸäÿØ ÿßŸÑÿ™ŸÜŸÅŸäÿ∞</p>
          </div>
        </div>
        
        <div className="stat-card">
          <div className="stat-icon">‚úÖ</div>
          <div className="stat-content">
            <h3>{stats?.closedCases || 0}</h3>
            <p>ÿßŸÑŸÇÿ∂ÿßŸäÿß ÿßŸÑŸÖÿ∫ŸÑŸÇÿ©</p>
          </div>
        </div>
      </div>

      <div className="dashboard-content">
        <div className="card">
          <div className="card-header flex-between">
            <h2>ÿßŸÑŸÇÿ∂ÿßŸäÿß ÿßŸÑÿ£ÿÆŸäÿ±ÿ©</h2>
            <Link to="/cases/new" className="btn btn-primary">+ ŸÇÿ∂Ÿäÿ© ÿ¨ÿØŸäÿØÿ©</Link>
          </div>
          
          {recentCases.length === 0 ? (
            <p>ŸÑÿß ÿ™Ÿàÿ¨ÿØ ŸÇÿ∂ÿßŸäÿß. ÿ£ŸÜÿ¥ÿ¶ ŸÇÿ∂Ÿäÿ™ŸÉ ÿßŸÑÿ£ŸàŸÑŸâ!</p>
          ) : (
            <table className="table">
              <thead>
                <tr>
                  <th>ÿ±ŸÇŸÖ ÿßŸÑŸÇÿ∂Ÿäÿ©</th>
                  <th>ÿßŸÑÿπŸÜŸàÿßŸÜ</th>
                  <th>ÿßŸÑÿπŸÖŸäŸÑ</th>
                  <th>ÿßŸÑÿ≠ÿßŸÑÿ©</th>
                  <th>ÿßŸÑÿ£ŸàŸÑŸàŸäÿ©</th>
                  <th>ÿßŸÑÿ•ÿ¨ÿ±ÿßÿ°ÿßÿ™</th>
                </tr>
              </thead>
              <tbody>
                {recentCases.map(caseItem => (
                  <tr key={caseItem._id}>
                    <td>{caseItem.caseNumber}</td>
                    <td>{caseItem.title}</td>
                    <td>{caseItem.client?.firstName} {caseItem.client?.lastName}</td>
                    <td><span className={`badge badge-${caseItem.status.toLowerCase().replace(' ', '-')}`}>{caseItem.status}</span></td>
