import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import './List.css';

function Cases() {
  const [cases, setCases] = useState([]);
  const [loading, setLoading] = useState(true);
  const [filters, setFilters] = useState({
    status: '',
    caseType: '',
    priority: ''
  });

  useEffect(() => {
    fetchCases();
  }, [filters]);

  const fetchCases = async () => {
    try {
      const queryParams = new URLSearchParams();
      if (filters.status) queryParams.append('status', filters.status);
      if (filters.caseType) queryParams.append('caseType', filters.caseType);
      if (filters.priority) queryParams.append('priority', filters.priority);

      const response = await axios.get(`/api/cases?${queryParams}`);
      setCases(response.data.cases);
      setLoading(false);
    } catch (error) {
      console.error('Error fetching cases:', error);
      setLoading(false);
    }
  };

  const handleFilterChange = (e) => {
    setFilters({
      ...filters,
      [e.target.name]: e.target.value
    });
  };

  const handleDelete = async (id) => {
    if (window.confirm('Are you sure you want to delete this case?')) {
      try {
        await axios.delete(`/api/cases/${id}`);
        fetchCases();
      } catch (error) {
        console.error('Error deleting case:', error);
        alert('Failed to delete case');
      }
    }
  };

  if (loading) {
    return <div className="loading">Loading cases...</div>;
  }

  return (
    <div className="container">
      <div className="page-header flex-between">
        <div>
          <h1>Cases</h1>
          <p>Manage all your legal cases</p>
        </div>
        <Link to="/cases/new" className="btn btn-primary">+ New Case</Link>
      </div>

      <div className="filters card">
        <div className="filters-grid">
          <div className="form-group">
            <label>Status</label>
            <select name="status" value={filters.status} onChange={handleFilterChange}>
              <option value="">All Statuses</option>
              <option value="Open">Open</option>
              <option value="In Progress">In Progress</option>
              <option value="Pending">Pending</option>
              <option value="Closed">Closed</option>
              <option value="Won">Won</option>
              <option value="Lost">Lost</option>
              <option value="Settled">Settled</option>
            </select>
          </div>

          <div className="form-group">
            <label>Case Type</label>
            <select name="caseType" value={filters.caseType} onChange={handleFilterChange}>
              <option value="">All Types</option>
              <option value="Criminal">Criminal</option>
              <option value="Civil">Civil</option>
              <option value="Family">Family</option>
              <option value="Corporate">Corporate</option>
              <option value="Real Estate">Real Estate</option>
              <option value="Immigration">Immigration</option>
              <option value="Intellectual Property">Intellectual Property</option>
              <option value="Labor">Labor</option>
              <option value="Tax">Tax</option>
              <option value="Other">Other</option>
            </select>
          </div>

          <div className="form-group">
            <label>Priority</label>
            <select name="priority" value={filters.priority} onChange={handleFilterChange}>
              <option value="">All Priorities</option>
              <option value="Low">Low</option>
              <option value="Medium">Medium</option>
              <option value="High">High</option>
              <option value="Urgent">Urgent</option>
            </select>
          </div>
        </div>
      </div>

      {cases.length === 0 ? (
        <div className="card text-center">
          <p>No cases found. Create your first case!</p>
        </div>
      ) : (
        <div className="card">
          <table className="table">
            <thead>
              <tr>
                <th>Case Number</th>
                <th>Title</th>
                <th>Client</th>
                <th>Type</th>
                <th>Status</th>
                <th>Priority</th>
                <th>Lawyer</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {cases.map(caseItem => (
                <tr key={caseItem._id}>
                  <td><strong>{caseItem.caseNumber}</strong></td>
                  <td>{caseItem.title}</td>
                  <td>{caseItem.client?.firstName} {caseItem.client?.lastName}</td>
                  <td>{caseItem.caseType}</td>
                  <td><span className={`badge badge-${caseItem.status.toLowerCase().replace(' ', '-')}`}>{caseItem.status}</span></td>
                  <td><span className={`badge badge-${caseItem.priority.toLowerCase()}`}>{caseItem.priority}</span></td>
                  <td>{caseItem.assignedLawyer?.name}</td>
                  <td>
                    <div className="action-buttons-inline">
                      <Link to={`/cases/${caseItem._id}`} className="btn btn-secondary">View</Link>
                      <Link to={`/cases/${caseItem._id}/edit`} className="btn btn-primary">Edit</Link>
                      <button onClick={() => handleDelete(caseItem._id)} className="btn btn-danger">Delete</button>
                    </div>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}
    </div>
  );
}

export default Cases;
